import './gallery.css';
import GalleryImage from '../../components/galleryImage/galleryImage';
import {useEffect, useState} from "react";
const Gallery = () => {
  const [state, setState] = useState([])
    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await fetch("https://knping.pl/api/v1/photos.php");
                const data = await result.json();
                setState(data)
            } catch (error) {
                console.error(error);
            }
        }

        fetchData();
    }, [])
  return (
        <div className="gallery">
          <div className="gallery__info">
            <h1>Galeria. Tu zobaczysz jak dzia≈Çamy</h1>
          </div>
          <div className="gallery__grid">
              <div className="gallery__left">
                  {
                      state.map( (a, i) =>
                          i % 2 === 0 ? <GalleryImage imgUrl={a.path} content={a.description} /> : ""
                      )
                  }
              </div>
              <div className="gallery__right">
                  {
                      state.map( (a, i) =>
                          i % 2 === 1 ? <GalleryImage imgUrl={a.path} content={a.description} /> : ""
                      )
                  }
              </div>
          </div>
        </div>
        )
}

export default Gallery;
